package menu.controller;import menu.service.CategoryService;import menu.service.CoachService;import menu.view.InputView;import menu.view.OutputView;public class MainController {    private final InputView inputView;    private final OutputView outputView;    private final CategoryService categoryService;    private final CoachService coachService;    private final MenuController menuController;    private final CoachController coachController;    public MainController() {        this.inputView = new InputView();        this.outputView = new OutputView();        this.categoryService = new CategoryService();        this.menuController = new MenuController(categoryService);        this.coachService = new CoachService();        this.coachController = new CoachController(inputView, coachService);    }    public void init() {        initCategoryService();        menuController.initMenu();        coachSave();    }    public void coachSave() {        coachController.init();    }    private void initCategoryService() {        categoryService.initCategory();    }}