package menu.controller;import menu.service.CategoryService;import menu.view.InputView;import menu.view.OutputView;public class MainController {    private final InputView inputView;    private final OutputView outputView;    private final CategoryService categoryService;    private final MenuController menuController;    public MainController() {        this.inputView = new InputView();        this.outputView = new OutputView();        this.categoryService = new CategoryService();        this.menuController = new MenuController(categoryService);    }    public void init() {        initCategoryService();        menuController.initMenu();    }    private void initCategoryService() {        categoryService.initCategory();    }}