package menu.controller;import menu.domain.Category;import menu.service.CategoryService;import menu.service.MenuService;public class MenuController {    private final CategoryService categoryService;    private final MenuService menuService;    public MenuController(CategoryService categoryService) {        this.categoryService = categoryService;        this.menuService = new MenuService(categoryService);    }    public void initMenu() {        menuService.initMenu();    }    public String recommend() {        Category category = categoryService.recommendCategories().get(0);        String menu = menuService.recommend(category);        return menu;    }}