package menu.domain;public enum Days {    MONDAY("월요일", 1),    TUESDAY("화요일", 2),    WEDNESDAY("수요일", 3),    THURSDAY("목요일", 4),    FRIDAY("금요일", 5);    private final String day;    private final int index;    Days(String day, int index) {        this.day = day;        this.index = index;    }    public String getDay() {        return day;    }    public int getIndex() {        return index;    }}