package menu.repository;import java.util.ArrayList;import java.util.List;import java.util.Optional;import menu.domain.Coach;import menu.exception.ExceptionMessage;public class CoachRepository {    private final List<Coach> coaches = new ArrayList<>();    public void addCoach(Coach coach) {        coaches.add(coach);    }    public Coach findByName(String name) {        Coach findCoach = coaches.stream().filter(coach -> coach.equalsByName(name))            .findAny()            .orElseThrow(() -> new IllegalArgumentException(ExceptionMessage.                NOT_FOUND_COACH_NAME.getErrorMessage()));        return findCoach;    }}