package menu.service;import java.util.ArrayList;import java.util.Arrays;import java.util.List;import java.util.stream.Collectors;import menu.domain.Coach;import menu.repository.CoachRepository;public class CoachService {    private final CoachRepository coachRepository;    public CoachService() {        this.coachRepository = new CoachRepository();    }    public void addCoaches(String inputCoachNames) {        List<String> coachNames = coachNameSeparator(inputCoachNames);        coachNames.stream().forEach(name -> coachRepository.addCoach(new Coach(name)));    }    private List<String> coachNameSeparator(String inputCoachNames) {        List<String> convert = Arrays.stream(inputCoachNames.split(","))            .collect(Collectors.toCollection(ArrayList::new));        return convert;    }}